import { connect } from "react-redux";
import { useState } from 'react';

const BASE_URL = "https://5e9935925eabe7001681c856.mockapi.io/api/v1/catalog/";

export const getCatalog = () => {
    var catalog = [];

    function setCatalog(data){ catalog = [...data]; }

    async function connect(catalog) {        
        fetch(BASE_URL)
        .then(res => res.json())
        .then((data, catalog) => {
            setCatalog([...data]);            
        });
        console.log("gei ", catalog);
    }    
    connect(catalog);
    return catalog;
}
