import {
  ADD_PRODUCT,
  REMOVE_PRODUCT,
  UPDATE_CART_PRODUCT_QUANTITY
} from '../actions/cartActions';

import { stringToReal } from "../../utils/converters";

export const INITIAL_STATE = {
  cart: [],
  total_value: 0.0,
  counter: 0
};

// falta melhorias
export default function cartReducer(state = INITIAL_STATE, action) {
  switch (action.type) {

    case ADD_PRODUCT:
      state.total_value += stringToReal(action.data.product_info.actual_price);
      return {
        ...state,
        cart: [...state.cart, action.data],
        counter: ++state.counter
      }

    case REMOVE_PRODUCT:  

      const newTotalValue = state.cart.find(product => 
        product.product_info.code_color === action.product_id
      ).product_info.actual_price;

      const newCart = state.cart
        .filter(product => product.product_info.code_color !== action.product_id);
      return {
        ...state,
        counter: --state.counter,
        cart: newCart,
        total_value: stringToReal(newTotalValue)
      }

    case UPDATE_CART_PRODUCT_QUANTITY:

      var q = state.cart.find(product =>
        product.product_info.code_color === action.product_id
      );

      if (q.quantity === 0) {
        if (action.value > 0) {
          state.cart.find(product =>
            product.product_info.code_color === action.product_id
          ).quantity += action.value;
        }
      }
      else {
        state.cart.find(product =>
          product.product_info.code_color === action.product_id
        ).quantity += action.value;
      }

      if (action.value > 0) {
        state.total_value += stringToReal(q.product_info.actual_price);
        ++state.counter;
      }
      else {
        state.total_value -= stringToReal(q.product_info.actual_price);
        --state.counter;
      }

      return {
        ...state,
      }

    default:
      return state;
  }
}
