import { connect } from "react-redux";
import { useState } from 'react';

const BASE_URL = "https://5e9935925eabe7001681c856.mockapi.io/api/v1/catalog/";

export const getCatalog = () => {
    const catalog = [];

    function setCatalog(data){ catalog = data; }

    async function connect(catalog) {        
        fetch(BASE_URL)
        .then(res => res.json())
        .then((data, catalog) => {
            const dataRes = [...data];

            return catalog = dataRes;
        });
        console.log("gei ", catalog);

        return catalog;
    }    
    let cu = connect(catalog);
    return cu;
}
