import React, { useState, useEffect } from 'react';
import Product from '../../components/Product/Product';
import { useSelector, useDispatch } from 'react-redux';

import { fetchProductsError, fetchProductsPending, fetchProductsSuccess } from '../../store/actions/actions';

import { fetchProducts } from '../../services/api';

import './Showcase.css';

const Showcase = () => {

	const dispatch = useDispatch();

	const { products } = useSelector(store => store);

	useEffect(() => {
		async function getProducts() {
			dispatch(fetchProductsPending());
			try {
				const data = await fetchProducts();
				dispatch(fetchProductsSuccess(data));
			}
			catch (err) {
				dispatch(fetchProductsError(err));
			}
		}
		getProducts();
		products.push({
			"name": "VESTIDO TRANSPASSE BOW",
			"style": "20002605",
			"code_color": "20002605_613",
			"color_slug": "tapecaria",
			"color": "TAPEÇARIA",
			"on_sale": false,
			"regular_price": "R$ 199,90",
			"actual_price": "R$ 199,90",
			"discount_percentage": "",
			"installments": "3x R$ 66,63",
			"image": "https://viniciusvinna.netlify.app/assets/api-fashionista/20002605_615_catalog_1.jpg",
			"sizes": [
				{
					"available": false,
					"size": "PP",
					"sku": "5807_343_0_PP"
				},
				{
					"available": true,
					"size": "P",
					"sku": "5807_343_0_P"
				},
				{
					"available": true,
					"size": "M",
					"sku": "5807_343_0_M"
				},
				{
					"available": true,
					"size": "G",
					"sku": "5807_343_0_G"
				},
				{
					"available": false,
					"size": "GG",
					"sku": "5807_343_0_GG"
				}
			]
		});
		console.log(" cuuuuu" + products)
	}
		, []);

	return (
		<section className="showcase">
			<div className="app__container">
				<div className="header__title">Numero de produtos {products.length}</div>
			</div>
			<div className="app__container">
				<div className="products__grid">
					{
						products.map((product, index) => (
							<Product key={index} data={product} />
						))
					}
				</div>
			</div>
		</section>
	);
}

export default Showcase;
