import {
    getCatalog
} from "../../services/api";

import { INITIAL_STATE } from "../initialState";
import {FETCH_PRODUCTS_PENDING, FETCH_PRODUCTS_SUCCESS, FETCH_PRODUCTS_ERROR} from '../actions';

const initialState = {
    pending: false,
    products: [],
    error: null
}

function reducer(state = INITIAL_STATE, action) {
    switch (action.type) {
        case "GET_PRODUCTS":            
            return {
                ...state,
                products: [],
            };
        default:
            return state;
    }
}


export default reducer;

