import {
    getCatalog
} from "../../services/api";

const INITIAL_STATE = {
    product: 'ok redux',
    products: []
}

function reducer(state = INITIAL_STATE, action) {
    switch (action.type) {
        case "GET_PRODUCTS":
            let catalog = getCatalog();
            return {
                ...state,
                products: catalog,
            };
        default:
            return state;
    }
}


export default reducer;

