import { getCatalog } from "../../services/api";

const INITIAL_STATE = {
    product: 'ok redux',
    products: []
}

function reducer(state=INITIAL_STATE, action) {
    switch (action.type) {
        case "GET_PRODUCTS":
            return {
                ...state,
                products: getCatalog().then(data),
            };            

        default:
            return state;
    }

}

export default reducer;

