import {
    connect
} from "react-redux";
import {
    useState
} from 'react';

const BASE_URL = "https://5e9935925eabe7001681c856.mockapi.io/api/v1/catalog/";

const loadProducts = async (products) => {

    const res = await fetch(BASE_URL);
    const data = await res.json();
    products = [...data];
    //return [...data];
}

export async function getCatalog() {
    var products = [];
    await loadProducts(products);
    return products;
}
